import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

// Define schema with Zod for validation
const questionSchema = z.object({
  emailAddress: z.string().email("Invalid email address"),
  interestingQuestionAsked: z.string().min(1, "This field is required"),
  interviewForIndustry: z.string().min(1, "This field is required"),
  interviewForCompany: z.string().min(1, "This field is required"),
  interviewForDesignation: z.string().min(1, "This field is required"),
  interviewForYearsExperience: z.number().positive("Must be a valid number"),
  interviewForYear: z.number().min(1900).max(new Date().getFullYear()),
  interestingQuestionGiven: z.string().min(1, "This field is required"),
  givenForIndustry: z.string().min(1, "This field is required"),
  givenForCompany: z.string().min(1, "This field is required"),
  givenForDesignation: z.string().min(1, "This field is required"),
  givenForYearsExperience: z.number().positive("Must be a valid number"),
  givenForYear: z.number().min(1900).max(new Date().getFullYear()),
  receiveUpdates: z.boolean(),
});

// Component
export default function FormPage() {
  const [isSuccessModalOpen, setIsSuccessModalOpen] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({
    resolver: zodResolver(questionSchema),
  });

  const onSubmit = async (data) => {
    try {
      const response = await fetch("http://localhost:4000/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        setIsSuccessModalOpen(true);
        reset(); // Clear the form
      } else {
        alert("Failed to submit form.");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div className="bg-gray-50 min-h-screen flex items-center justify-center p-6">
      <div className="bg-white w-full max-w-3xl rounded-lg shadow-lg p-8">
        <h1 className="text-2xl font-semibold text-gray-800 mb-6">Mock Vani Interview Survey</h1>
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Email Address */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Email Address</label>
            <input
              type="email"
              {...register("emailAddress")}
              className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="your@email.com"
            />
            {errors.emailAddress && <p className="text-red-500 text-sm">{errors.emailAddress.message}</p>}
          </div>

          {/* Interviewee Questions Section */}
          <div>
            <h2 className="text-lg font-semibold text-gray-700 mb-4">As an Interviewee</h2>
            <label className="block text-sm font-medium text-gray-700">
              Most interesting interview question you’ve been asked
            </label>
            <textarea
              {...register("interestingQuestionAsked")}
              className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="What’s the most interesting question you’ve been asked?"
            />
            {errors.interestingQuestionAsked && (
              <p className="text-red-500 text-sm">{errors.interestingQuestionAsked.message}</p>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Industry</label>
                <input
                  type="text"
                  {...register("interviewForIndustry")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., Technology"
                />
                {errors.interviewForIndustry && <p className="text-red-500 text-sm">{errors.interviewForIndustry.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Company</label>
                <input
                  type="text"
                  {...register("interviewForCompany")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., Mock Vani"
                />
                {errors.interviewForCompany && <p className="text-red-500 text-sm">{errors.interviewForCompany.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Designation</label>
                <input
                  type="text"
                  {...register("interviewForDesignation")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., Software Engineer"
                />
                {errors.interviewForDesignation && <p className="text-red-500 text-sm">{errors.interviewForDesignation.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Years of Experience</label>
                <input
                  type="number"
                  {...register("interviewForYearsExperience")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                {errors.interviewForYearsExperience && <p className="text-red-500 text-sm">{errors.interviewForYearsExperience.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Year</label>
                <input
                  type="number"
                  {...register("interviewForYear")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                {errors.interviewForYear && <p className="text-red-500 text-sm">{errors.interviewForYear.message}</p>}
              </div>
            </div>
          </div>

          {/* Interviewer Questions Section */}
          <div>
            <h2 className="text-lg font-semibold text-gray-700 mb-4">As an Interviewer</h2>
            <label className="block text-sm font-medium text-gray-700">
              Most interesting interview question you’ve asked
            </label>
            <textarea
              {...register("interestingQuestionGiven")}
              className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="What’s the most interesting question you’ve asked?"
            />
            {errors.interestingQuestionGiven && (
              <p className="text-red-500 text-sm">{errors.interestingQuestionGiven.message}</p>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Industry</label>
                <input
                  type="text"
                  {...register("givenForIndustry")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., Technology"
                />
                {errors.givenForIndustry && <p className="text-red-500 text-sm">{errors.givenForIndustry.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Company</label>
                <input
                  type="text"
                  {...register("givenForCompany")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., Mock Vani"
                />
                {errors.givenForCompany && <p className="text-red-500 text-sm">{errors.givenForCompany.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Designation</label>
                <input
                  type="text"
                  {...register("givenForDesignation")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                {errors.givenForDesignation && <p className="text-red-500 text-sm">{errors.givenForDesignation.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Years of Experience</label>
                <input
                  type="number"
                  {...register("givenForYearsExperience")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                {errors.givenForYearsExperience && <p className="text-red-500 text-sm">{errors.givenForYearsExperience.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Year</label>
                <input
                  type="number"
                  {...register("givenForYear")}
                  className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                {errors.givenForYear && <p className="text-red-500 text-sm">{errors.givenForYear.message}</p>}
              </div>
            </div>
          </div>

          {/* Receive Updates Checkbox */}
          <div>
            <label className="flex items-center">
              <input
                type="checkbox"
                {...register("receiveUpdates")}
                className="h-4 w-4 border-gray-300 rounded focus:ring-blue-500"
              />
              <span className="ml-2 text-sm text-gray-700">Receive Updates</span>
            </label>
          </div>

          {/* Submit Button */}
          <div className="flex justify-end">
            <button
              type="submit"
              className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
            >
              Submit Responses
            </button>
          </div>
        </form>

        {/* Success Modal */}
        {isSuccessModalOpen && (
          <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-md shadow-md">
              <h2 className="text-lg font-semibold text-gray-800">Thank you!</h2>
              <p className="text-sm text-gray-600">Your response has been recorded successfully.</p>
              <button
                onClick={() => setIsSuccessModalOpen(false)}
                className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
