import React, { useEffect, useState } from "react";
import "../styles/InsightsPage.css";

export default function InsightsPage() {
  const [insights, setInsights] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch insights from the backend
    async function fetchInsights() {
      try {
        const response = await fetch("http://localhost:4000/insights");
        if (!response.ok) {
          throw new Error("Failed to fetch insights");
        }
        const data = await response.json();
        setInsights(data);
      } catch (error) {
        console.error("Error fetching insights:", error);
      } finally {
        setLoading(false);
      }
    }
    fetchInsights();
  }, []);

  if (loading) return <div>Loading insights...</div>;
  if (!insights) return <div>Failed to load insights.</div>;

  return (
    <div className="insights-page">
      <h1>Insights</h1>

      <div className="insight">
        <h2>Total Submissions</h2>
        <p>{insights.totalSubmissions}</p>
      </div>

      <div className="insight">
        <h2>Industry Spread</h2>
        <ul>
          {insights.industrySpread.map((industry, index) => (
            <li key={index}>
              {industry.InterviewForIndustry}: {industry.count}
            </li>
          ))}
        </ul>
      </div>

      <div className="insight">
        <h2>Top Companies</h2>
        <ul>
          {insights.topCompanies.map((company, index) => (
            <li key={index}>
              {company.InterviewForCompany}: {company.count}
            </li>
          ))}
        </ul>
      </div>

      <div className="insight">
        <h2>Most Interesting Questions</h2>
        <ul>
          {insights.topQuestions.map((question, index) => (
            <li key={index}>
              "{question.InterestingQuestionAsked}" from {question.InterviewForCompany}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
