import React, { useState } from "react";
import QuestionSection from "./QuestionSection";
import SuccessModal from "./SuccessModal";
import "../styles/FormPage.css";

export default function FormPage() {
  const [formData, setFormData] = useState({
    email: "",
    askedQuestion: {
      question: "",
      industry: "",
      company: "",
      designation: "",
      yearsExperience: "",
      year: "",
    },
    receivedQuestion: {
      question: "",
      industry: "",
      company: "",
      designation: "",
      yearsExperience: "",
      year: "",
    },
    receiveUpdates: false,
  });

  const [isSuccessModalOpen, setIsSuccessModalOpen] = useState(false);

  const handleChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value,
      },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data Submitted:", formData); // Replace with actual API call
    setIsSuccessModalOpen(true);
  };

  return (
    <div className="form-page">
      <header className="form-header">
        <h1>Mock Vani Interview Survey</h1>
        <p>Share the most interesting interview questions youâ€™ve encountered!</p>
      </header>

      <main className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-section">
            <label>Email Address</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              placeholder="Enter your email"
              required
            />
          </div>

          {/* Interviewer Section */}
          <QuestionSection
            title="As an Interviewer"
            section="askedQuestion"
            data={formData.askedQuestion}
            onChange={handleChange}
          />

          {/* Interviewee Section */}
          <QuestionSection
            title="As an Interviewee"
            section="receivedQuestion"
            data={formData.receivedQuestion}
            onChange={handleChange}
          />

          <div className="form-section">
            <label>
              <input
                type="checkbox"
                checked={formData.receiveUpdates}
                onChange={(e) => setFormData({ ...formData, receiveUpdates: e.target.checked })}
              />
              Receive Updates
            </label>
          </div>

          <button type="submit" className="submit-button">
            Submit Responses
          </button>
        </form>
      </main>

      <footer className="form-footer">
        <p>&copy; {new Date().getFullYear()} Mock Vani. All rights reserved.</p>
      </footer>

      {isSuccessModalOpen && (
        <SuccessModal
          message="Your response has been recorded successfully!"
          onClose={() => setIsSuccessModalOpen(false)}
        />
      )}
    </div>
  );
}
