import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

// Define schema with Zod for validation
const questionSchema = z.object({
  emailAddress: z.string().email("Invalid email address"),
  interestingQuestionAsked: z.string().min(1, "This field is required"),
  interviewForIndustry: z.string().min(1, "This field is required"),
  interviewForCompany: z.string().min(1, "This field is required"),
  interviewForDesignation: z.string().min(1, "This field is required"),
  interviewForYearsExperience: z.number().positive("Must be a valid number"),
  interviewForYear: z.number().min(1900).max(new Date().getFullYear()),
  interestingQuestionGiven: z.string().min(1, "This field is required"),
  givenForIndustry: z.string().min(1, "This field is required"),
  givenForCompany: z.string().min(1, "This field is required"),
  givenForDesignation: z.string().min(1, "This field is required"),
  givenForYearsExperience: z.number().positive("Must be a valid number"),
  givenForYear: z.number().min(1900).max(new Date().getFullYear()),
  receiveUpdates: z.boolean(),
});

// Component
export default function FormPage() {
  const [isSuccessModalOpen, setIsSuccessModalOpen] = useState(false);

  // React Hook Form setup
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({
    resolver: zodResolver(questionSchema),
  });

  const onSubmit = async (data) => {
    try {
      const response = await fetch("http://localhost:4000/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        setIsSuccessModalOpen(true);
        reset(); // Clear the form
      } else {
        alert("Failed to submit form.");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6 md:p-8">
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        {/* Email Address */}
        <div>
          <label className="block text-sm font-medium text-gray-700">Email Address</label>
          <input
            type="email"
            {...register("emailAddress")}
            className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="your@email.com"
          />
          {errors.emailAddress && <p className="text-red-500 text-sm">{errors.emailAddress.message}</p>}
        </div>

        {/* Questions Section */}
        <div className="space-y-6">
          {/* Question Received */}
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Most interesting question you’ve been asked
            </label>
            <textarea
              {...register("interestingQuestionAsked")}
              className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="What’s the most interesting question you’ve been asked?"
            />
            {errors.interestingQuestionAsked && (
              <p className="text-red-500 text-sm">{errors.interestingQuestionAsked.message}</p>
            )}
          </div>

          {/* Question Asked */}
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Most interesting question you’ve asked
            </label>
            <textarea
              {...register("interestingQuestionGiven")}
              className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="What’s the most interesting question you’ve asked?"
            />
            {errors.interestingQuestionGiven && (
              <p className="text-red-500 text-sm">{errors.interestingQuestionGiven.message}</p>
            )}
          </div>
        </div>

        {/* Additional Inputs */}
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Interview Industry</label>
            <input
              type="text"
              {...register("interviewForIndustry")}
              className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="e.g., Technology"
            />
            {errors.interviewForIndustry && <p className="text-red-500 text-sm">{errors.interviewForIndustry.message}</p>}
          </div>
          {/* Add similar fields for Company, Designation, Years of Experience, etc. */}
        </div>

        {/* Receive Updates Checkbox */}
        <div>
          <label className="flex items-center">
            <input
              type="checkbox"
              {...register("receiveUpdates")}
              className="h-4 w-4 border-gray-300 rounded focus:ring-blue-500"
            />
            <span className="ml-2 text-sm text-gray-700">Receive Updates</span>
          </label>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end">
          <button
            type="submit"
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
          >
            Submit Responses
          </button>
        </div>
      </form>

      {/* Success Modal */}
      {isSuccessModalOpen && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-md shadow-md">
            <h2 className="text-lg font-semibold text-gray-800">Thank you!</h2>
            <p className="text-sm text-gray-600">Your response has been recorded successfully.</p>
            <button
              onClick={() => setIsSuccessModalOpen(false)}
              className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
